func (db *MockDB) SelectAll(ctx context.Context) ([]dms.DMS, error) {
	dmsArray := []dms.DMS{}
	dmsArray = append(dmsArray, testDMS(dms.ApprovedStatus))
	if ctx.Value("DBSelectAll") != nil {
		failDBLog := ctx.Value("DBSelectAll").(bool)

		if failDBLog {
			return []dms.DMS{}, errors.New("Error Select All")
		} else {
			return dmsArray, nil
		}
	}
	return []dms.DMS{}, nil
}

func (db *MockDB) SelectByID(ctx context.Context, id string) (dms.DMS, error) {
	var d dms.DMS
	switch id {
	case "1":
		d = testDMS(dms.ApprovedStatus)
	case "2":
		d = testDMS(dms.PendingStatus)
	case "3":
		d = testDMS(dms.RevokedStatus)
	case "4":
		d = testDMS(dms.DeniedStatus)
	}

	if ctx.Value("DBCsrBase64") != nil {
		failDBLog := ctx.Value("DBCsrBase64").(bool)
		if failDBLog {
			d.CsrBase64 = ""
		}
	}

	if ctx.Value("DBSelectByID") != nil {
		failDBLog := ctx.Value("DBSelectByID").(bool)

		if failDBLog {
			return dms.DMS{}, errors.New("Error Select By ID")
		} else {
			return d, nil
		}
	}
	return dms.DMS{}, nil
}

