package mocks

import (
	"context"
	"crypto/x509"
	"errors"

	"github.com/go-kit/kit/log"
	lamassuca "github.com/lamassuiot/lamassu-ca/pkg/client"
)

type LamassuCaClientConfig struct {
	client lamassuca.BaseClient
	logger log.Logger
}

func NewLamassuCaClientMock(logger log.Logger) (lamassuca.LamassuCaClient, error) {

	return &LamassuCaClientConfig{
		client: lamassuca.NewBaseClient(nil, nil),
		logger: logger,
	}, nil
}

func (c *LamassuCaClientConfig) GetCAs(ctx context.Context, caType string) (lamassuca.Certs, error) {

	var CAs lamassuca.Certs
	var caList []lamassuca.Cert
	newCA := testCert()
	caList = append(caList, newCA)
	CAs = lamassuca.Certs{Certs: caList}
	failDB := ctx.Value("DBShouldFail").(bool)

	if failDB {

		return CAs, errors.New("Error in client request")
	} else {
		return CAs, nil
	}
}

func (c *LamassuCaClientConfig) SignCertificateRequest(ctx context.Context, signingCaName string, csr *x509.CertificateRequest, caType string, signVerbatim bool) (*x509.Certificate, error) {
	failDB := ctx.Value("SignCertificateRequestFail").(bool)

	if failDB {
		c := x509.Certificate{}
		c.Subject.CommonName = csr.Subject.CommonName
		return &c, errors.New("Error revoking certificate")
	} else {
		c := x509.Certificate{}
		c.Subject.CommonName = csr.Subject.CommonName
		c.Issuer.CommonName = signingCaName
		return &c, nil
	}
}

func (c *LamassuCaClientConfig) RevokeCert(ctx context.Context, IssuerName string, serialNumberToRevoke string, caType string) error {
	failDB := ctx.Value("RevokeCertShouldFail").(bool)

	if failDB {
		return errors.New("Error revoking certificate")
	} else {

		return nil
	}
}

func GetCert(ctx context.Context, IssuerName string, SerialNumber string, caType string) (lamassuca.Cert, error) {
	return lamassuca.Cert{}, nil
}

func (c *LamassuCaClientConfig) GetCert(ctx context.Context, IssuerName string, SerialNumber string, caType string) (lamassuca.Cert, error) {
	failDB := ctx.Value("GetCertFail").(bool)

	if ctx.Value("DateFormatError") != nil {
		failDF := ctx.Value("DateFormatError").(bool)
		if failDF {
			return lamassuca.Cert{}, errors.New("Error getting certificate")
		} else {
			c := testCert()
			c.ValidFrom = ""
			c.ValidTo = ""
			return c, nil
		}
	} else {
		if failDB {
			return lamassuca.Cert{}, errors.New("Error getting certificate")
		} else {
			return testCert(), nil
		}
	}

}

func testCert() lamassuca.Cert {

	serialNumber := "1E-6A-EC-C2-05-64-EF-65-66-7F-5B-AE-7B-B4-15-25-19-E8-2C-87"

	keyMetadata := lamassuca.KeyInfo{
		KeyType:     "RSA",
		KeyBits:     4096,
		KeyStrength: "high",
	}

	subject := lamassuca.Subject{
		CN: "CA",
		OU: "ZPD",
		O:  "IKL",
		L:  "Arrasate",
		ST: "Gipuzkoa",
		C:  "ES",
	}

	certContent := lamassuca.CertContent{
		CerificateBase64: "",
		PublicKeyBase64:  "LS0tLS1CRUdJTiBQVUJMSUMgS0VZLS0tLS0KTUZrd0V3WUhLb1pJemowQ0FRWUlLb1pJemowREFRY0RRZ0FFNWkxcWZ5WVNsS2lrd0g4RmZIb0FsVVhOOEZRNwpoTU4wRFpOTy9XN2JITjg1UWlnT1l5VDVtY1gyV2wybUk1RC9MUE9QSndJeDdWWXJsWVNQTE5uZ3Z3PT0KLS0tLS1FTkQgUFVCTElDIEtFWS0tLS0tCg==",
	}

	cert := lamassuca.Cert{
		Status:       "issued",
		SerialNumber: serialNumber,
		CAName:       "CA",
		KeyMetadata:  keyMetadata,
		Subject:      subject,
		CertContent:  certContent,
		ValidFrom:    "2021-02-16 16:42:31.3773933 +0000 UTC",
		ValidTo:      "2022-01-01 16:42:31.3773933 +0000 UTC",
	}
	return cert
}
